// These are the days of the week
const arrayOfDays = [
	'Sunday',
	'Monday',
	'Tuesday',
	'Wednesday',
	'Thursday',
	'Friday',
	'Saturday',
];

/**
 * This method takes the date and returns
 * the name of the day provided by param
 * @param  {Date} date
 * @return {string}
 */
function getDayWhenBorn(date) {
	return arrayOfDays[date.getDay()];
}

module.exports = {
	getDayWhenBorn: getDayWhenBorn,
};
