const expect = require('chai').expect;
const calculator = require('../index');

describe('when a valid date is provided', function() {
	it('should return the name of day', function() {
		const date = new Date('1990,10,01');
		const dayWhenBorn = calculator.getDayWhenBorn(date);
		expect(dayWhenBorn).to.equal('Monday');
	});
});
